import { Component, OnInit } from '@angular/core';
import { Cliente } from './cliente';
import { ClienteService } from './cliente.service';
import { Router, ActivatedRoute } from '@angular/router';
import Swal from 'sweetalert2'


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
})
export class FormComponent implements OnInit {
   cliente: Cliente = new Cliente()
  titulo:string = "Crear Cliente"
  errors: string[] | undefined;



  constructor(private clienteService: ClienteService, 
    private router: Router,
    private activatedRoute: ActivatedRoute) {}


  ngOnInit() {
    this.cargarCliente()
  }

  cargarCliente(): void {
    this.activatedRoute.params.subscribe(params => {
      let id = params ['id']
      if (id){
        this.clienteService.getCliente(id).subscribe((cliente) => this.cliente = cliente)
      }
    })

  }

  create(): void{
    this.clienteService.create(this.cliente).subscribe(
      cliente => {
        this.router.navigate(['/clientes'])
        Swal.fire('Nuevo cliente', `El cliente ${cliente.nombre} ha sido creado con éxito `, 'success')
      },
      err => { 
        this.errors = err.error.errors as string[];
        console.error('Código del error desde el backend: ' + err.status);
        console.error(err.error.errors);
      }
    );

  }

  update(): void {
    this.clienteService.update(this.cliente).subscribe( 
      json => {
      this.router.navigate(['/clientes'])
      Swal.fire('Nuevo cliente', ` ${json.mensaje}: ${json.cliente.nombre}`, 'success')

    },
    err => { 
      this.errors = err.error.errors as string[];
      console.error('Código del error desde el backend: ' + err.status);
      console.error(err.error.errors);
    }

    )
  }

}
<ul class="alert alert-danger" *ngIf="errors && errors.length > 0">
    <li *ngFor="let err of errors">
        {{err}}
    </li>
</ul>

<div class="card bg-dark text-white">
<div class="card-header">{{titulo}}</div>
<div class="card-body">


<form #clienteForm="ngForm">
    <div class="form-group row">
        <label for="nombre" class="col-form-label col-sm-2">Nombre</label>
          <div class="col-sm-6">
            <input type="text" class="form-control" [(ngModel)]="cliente.nombre" name="nombre" #nombre="ngModel"> <!--required minLenght="4"-->
              <div class="alert alert-danger" *ngIf="nombre.invalid && (nombre.dirty || nombre.touched)">
                 <div *ngIf="nombre.errors?.['required']">
                    Nombre es requerido 
                 </div>
                   <div *ngIf="nombre.errors?.['required']">
                       Nombre debe tener al menos 4 caracteres
                   </div>
                 </div>
               </div>
    </div>

        <div class="form-group row">
            <label for="apellido" class="col-form-label col-sm-2">Apellido</label>
                <div class="col-sm-6">
                    <input type="text" class="form-control" [(ngModel)]="cliente.apellido" name="apellido" #apellido="ngModel"><!--required-->
                    <div class="alert alert-danger" *ngIf="apellido.invalid && (apellido.dirty || apellido.touched)">
                        <div *ngIf="apellido.errors?.['required']">
                           Apellido es requerido 
                        </div>
                    </div>
               </div>
        </div>

        <div class="form-group row">
            <label for="email" class="col-form-label col-sm-2">Email</label>
                <div class="col-sm-6">
                    <input type="text" class="form-control" [(ngModel)]="cliente.email" name="email" #email="ngModel"><!--required email-->
                    <div class="alert alert-danger" *ngIf="email.invalid && (email.dirty || email.touched)">
                        <div *ngIf="email.errors?.['required']">
                           Email es requerido 
                        </div>
                        <div *ngIf="email.errors?.['email']">
                            Email debe tener un formato valido 
                         </div>
                    </div>
                 </div>
        </div>

        <div class="form-group row">
            <label for="createAt" class="col-form-label col-sm-2">Fecha</label>
                <div class="col-sm-6">
                    <input type="text" class="form-control" [(ngModel)]="cliente.createAt" name="createAt">
               </div>
        </div>


     <div class="form-group row">
            <div class="col-sm-6">
                <button class="btn btn-primary" role="button" (click)='create()' *ngIf="!cliente.id else elseBlock" [disabled]="!clienteForm.form.valid">Crear</button>

                <ng-template #elseBlock>

                <button class="btn btn-primary" role="button" (click)='update()' [disabled]="!clienteForm.form.valid">Editar </button>
                </ng-template>
            </div>
        </div>
